import React, { useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';

export default function ResetPassword() {
  const [params] = useSearchParams();
  const token = params.get('token');
  const email = params.get('email');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState('');
  const navigate = useNavigate();

  async function submit(e) {
    e.preventDefault();
    setMsg('');
    try {
      const res = await fetch(
        `${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/api/auth/reset-password`,
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, token, password }),
        }
      );
      const data = await res.json();
      if (res.ok) {
        setMsg('‚úÖ Password changed successfully! Redirecting...');
        setTimeout(() => navigate('/'), 2000);
      } else {
        setMsg(`‚ö†Ô∏è ${data.error || 'Something went wrong!'}`);
      }
    } catch (err) {
      setMsg('‚ùå Network error, please try again later.');
    }
  }

  // if token or email is missing
  if (!token || !email)
    return <div className="alert alert-danger mt-5 text-center">Invalid or expired reset link!</div>;

  return (
    <div className="card p-4 mx-auto mt-5 shadow" style={{ maxWidth: 480 }}>
      <h4 className="mb-3 text-center">üîí Reset Password</h4>
      <form onSubmit={submit}>
        <div className="mb-3">
          <label className="form-label">New Password</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your new password"
            required
            minLength={6}
          />
        </div>
        <button type="submit" className="btn btn-success w-100">
          Change Password
        </button>
      </form>

      {msg && <div className="alert alert-info text-center mt-3">{msg}</div>}

      <div className="mt-2 text-muted small text-center">
        Link valid only for 1 hour ‚è≥
      </div>
    </div>
  );
}
