// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import ForgotPassword from './components/ForgotPassword';
import ResetPassword from './components/ResetPassword';

export default function App() {
  return (
    <BrowserRouter>
      <div className="app-shell">
        <header className="header">
          <div className="container d-flex align-items-center">
            <div className="brand">Secure Account — Password Reset</div>
            <div className="ms-auto small-muted">Built with React & Node</div>
          </div>
        </header>

        <main className="main-wrap">
          <div className="container">
            <Routes>
              <Route path="/" element={<ForgotPassword />} />
              <Route path="/reset-password" element={<ResetPassword />} />
            </Routes>
          </div>
        </main>

        <footer className="text-center small-muted py-3">© {new Date().getFullYear()} — Password Reset Demo</footer>
      </div>
    </BrowserRouter>
  );
}
