import React, { useState } from 'react';


export default function ForgotPassword(){
const [email, setEmail] = useState('');
const [msg, setMsg] = useState('');


async function submit(e){
e.preventDefault();
setMsg('');
try{
const res = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/api/auth/request-reset`, {
method: 'POST', headers: {'Content-Type':'application/json'}, body: JSON.stringify({ email })
});
const data = await res.json();
if (res.ok) setMsg('Reset link sent. Check your email.');
else setMsg(data.error || 'Error');
} catch(err){ setMsg('Network error'); }
}


return (
<div className="card p-4 mx-auto" style={{maxWidth: 480}}>
<h4>Forgot password</h4>
<form onSubmit={submit}>
<div className="mb-3">
<label className="form-label">Email</label>
<input type="email" className="form-control" value={email} onChange={e=>setEmail(e.target.value)} required/>
</div>
<button className="btn btn-primary">Send reset link</button>
</form>
{msg && <div className="mt-3 alert alert-info">{msg}</div>}
<div className="mt-2 text-muted small">Use seeded account <strong>test@example.com</strong> for testing.</div>
</div>
)
}
