import React, { useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';

export default function ResetPassword() {
  const [params] = useSearchParams();
  const token = params.get('token');
  const email = params.get('email');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Basic password validation: length >= 6 and contains a number (you can adjust)
  const isValid = password.length >= 6 && /\d/.test(password) && password === confirm;

  async function submit(e) {
    e.preventDefault();
    setMsg('');
    if (!isValid) {
      setMsg('Password must be 6+ chars, contain a number, and both fields must match.');
      return;
    }
    setLoading(true);
    try {
      const res = await fetch(
        `${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/api/auth/reset-password`,
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, token, password }),
        }
      );
      const data = await res.json();
      setLoading(false);
      if (res.ok) {
        setMsg('✅ Password changed successfully! Redirecting...');
        setTimeout(() => navigate('/'), 2000);
      } else {
        setMsg(`⚠️ ${data.error || 'Something went wrong!'}`);
      }
    } catch (err) {
      setLoading(false);
      setMsg('❌ Network error, please try again later.');
    }
  }

  if (!token || !email)
    return <div className="alert alert-danger mt-5 text-center">Invalid or expired reset link!</div>;

  return (
    <div className="card p-4 mx-auto mt-5 shadow" style={{ maxWidth: 480 }}>
     <h4 className="mb-3 text-center"><i className="bi bi-lock-fill me-2"></i> Reset Password</h4>

      <form onSubmit={submit}>
        <div className="mb-3">
          <label className="form-label">New Password</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your new password"
            required
            minLength={6}
            aria-label="New password"
          />
          <small className="form-text text-muted">
            At least 6 characters and include a number.
          </small>
        </div>

        <div className="mb-3">
          <label className="form-label">Confirm Password</label>
          <input
            type="password"
            className="form-control"
            value={confirm}
            onChange={(e) => setConfirm(e.target.value)}
            placeholder="Re-enter password"
            required
            aria-label="Confirm password"
          />
        </div>

        <button type="submit" className="btn btn-success w-100" disabled={!isValid || loading}>
          {loading ? 'Saving...' : 'Change Password'}
        </button>
      </form>

      {msg && <div className="alert alert-info text-center mt-3">{msg}</div>}

      <div className="mt-2 text-muted small text-center">Link valid only for 1 hour ⏳</div>
    </div>
  );
}
