// src/components/HeroLeft.jsx
import React from 'react';
import { Link } from 'react-router-dom';

export default function HeroLeft() {
  return (
    <div className="hero-left">
      <div className="hero-inner">
        <div className="hero-top">
         
          <div className="hero-headline">
            <div className="brand-title">Secure Account — Password Reset</div>
            <div className="brand-sub">Welcome — Manage your account securely</div>
          </div>
        </div>

    

        <div className="hero-note">
          <p>Use the links to navigate each page. Each link opens the relevant screen on the right panel.</p>
          <small className="muted">Tip: use the seeded account <strong>test@example.com</strong> to demo the reset flow.</small>
        </div>

      </div>
    </div>
  );
}
