// src/components/ForgotPassword.jsx
import React, { useState } from 'react';

export default function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState({ type: '', text: '' });
  const [loading, setLoading] = useState(false);

  function validateEmail(e) {
    // simple email check
    return /\S+@\S+\.\S+/.test(e);
  }

  async function submit(e) {
    e.preventDefault();
    setStatus({ type: '', text: '' });
    if (!validateEmail(email)) {
      setStatus({ type: 'error', text: 'Please enter a valid email address.' });
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/api/auth/request-reset`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      const data = await res.json();
      setLoading(false);
      if (res.ok) {
        // If Ethereal preview is returned it'll be in data.previewUrl
        const message = data.previewUrl ? 'Reset link created (preview available).' : 'Reset link sent to your email (if registered).';
        setStatus({ type: 'success', text: message });
        // helpful: show preview link when available
        if (data.previewUrl) {
          setStatus({ type: 'success', text: `${message} Preview: ${data.previewUrl}` });
        }
      } else {
        setStatus({ type: 'error', text: data.error || 'Something went wrong. Try again later.' });
      }
    } catch (err) {
      setLoading(false);
      setStatus({ type: 'error', text: 'Network error. Please try again.' });
    }
  }

  return (
    <div className="form-card mx-auto">
      <div className="d-flex align-items-center mb-2">
        <div className="me-3 display-6" style={{fontSize: '1.45rem'}}>üîê</div>
        <div>
          <div className="form-title">Forgot password</div>
          <div className="form-sub">Enter your account email and we'll send a secure reset link.</div>
        </div>
      </div>

      <form onSubmit={submit} noValidate>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            id="email"
            type="email"
            className="form-control"
            placeholder="you@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            aria-describedby="emailHelp"
          />
          <div id="emailHelp" className="form-text small-muted">We will never share your email.</div>
        </div>

        <button type="submit" className="btn btn-primary w-100" disabled={loading}>
          {loading ? 'Sending‚Ä¶' : 'Send reset link'}
        </button>
      </form>

      <div className="mt-3 small-muted">
        Use seeded test account <strong>test@example.com</strong> for demo (Ethereal preview).
      </div>

      {status.text && (
        <div className={`mt-3 alert ${status.type === 'success' ? 'alert-success' : 'alert-danger'}`} role="alert">
          {status.text}
        </div>
      )}
    </div>
  );
}
