// src/components/ResetPassword.jsx
import React, { useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';

export default function ResetPassword() {
  const [params] = useSearchParams();
  const token = params.get('token');
  const email = params.get('email');

  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const valid = password.length >= 6 && /\d/.test(password) && password === confirm;

  async function submit(e) {
    e.preventDefault();
    setMsg('');
    if (!valid) {
      setMsg('Password must be 6+ chars, include a number, and both fields must match.');
      return;
    }
    setLoading(true);
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/api/auth/reset-password`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, token, password }),
      });
      const data = await res.json();
      setLoading(false);
      if (res.ok) {
        setMsg('‚úÖ Password changed successfully. Redirecting to home...');
        setTimeout(() => navigate('/'), 1800);
      } else {
        setMsg(data.error || 'Something went wrong.');
      }
    } catch (err) {
      setLoading(false);
      setMsg('Network error.');
    }
  }

  if (!token || !email) {
    return <div className="form-card mx-auto text-center"><div className="form-title">Invalid or expired link</div></div>;
  }

  return (
    <div className="form-card mx-auto">
      <div className="d-flex align-items-center mb-3">
        <div className="me-3 display-6" style={{fontSize: '1.45rem'}}>üîí</div>
        <div>
          <div className="form-title">Reset Password</div>
          <div className="form-sub">Enter a new secure password for <strong>{decodeURIComponent(email)}</strong></div>
        </div>
      </div>

      <form onSubmit={submit}>
        <div className="mb-3">
          <label className="form-label">New password</label>
          <input type="password" className="form-control" value={password} onChange={(e)=>setPassword(e.target.value)} required placeholder="At least 6 characters and a number" />
        </div>

        <div className="mb-3">
          <label className="form-label">Confirm password</label>
          <input type="password" className="form-control" value={confirm} onChange={(e)=>setConfirm(e.target.value)} required />
        </div>

        <button type="submit" className="btn btn-success w-100" disabled={!valid || loading}>
          {loading ? 'Saving‚Ä¶' : 'Change Password'}
        </button>
      </form>

      {msg && <div className="mt-3 alert alert-info text-center">{msg}</div>}

      <div className="mt-2 small-muted text-center">Link valid for 1 hour ‚è≥</div>
    </div>
  );
}
