// src/components/HeroLeft.jsx
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

export default function HeroLeft() {
  const loc = useLocation();
  const active = (path) => (loc.pathname === path ? 'hero-nav-item active' : 'hero-nav-item');

  return (
    <div className="hero-left">
      <div className="hero-inner">
        <div className="hero-top">
          <img src="/mnt/data/pwe1.png" alt="logo" className="hero-logo" />
          <div className="hero-headline">
            <div className="brand-title">Secure Account — Password Reset</div>
            <div className="brand-sub">Welcome — Manage your account securely</div>
          </div>
        </div>

        <nav className="hero-nav" aria-label="primary">
          <Link to="/dashboard" className={active('/dashboard')}>Dashboard</Link>
          <Link to="/register"  className={active('/register')}>Sign up</Link>
          <Link to="/login"     className={active('/login')}>Login</Link>
          <Link to="/"          className={active('/')}>Forgot password</Link>
        </nav>

        <div className="hero-note">
          <p>Use the links to the left to navigate each page. Each link opens the relevant screen on the right panel.</p>
          <small className="muted">Tip: use the seeded account <strong>test@example.com</strong> to demo the reset flow.</small>
        </div>

      </div>
    </div>
  );
}
