// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import ForgotPassword from './components/ForgotPassword';
import ResetPassword from './components/ResetPassword';
import Register from './components/Register';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import ProtectedRoute from './components/ProtectedRoute';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function App() {
  const token = typeof window !== 'undefined' && localStorage.getItem('token');

  return (
    <BrowserRouter>
      <div className="app-shell">
        <header className="header" style={{ position: 'relative' }}>
          {/* optional left aligned dashboard link (keeps quick access) */}
          <div style={{ position:'absolute', left:18, top:20 }}>
            {token ? <Link to="/dashboard" className="nav-link" style={{ color: 'var(--accent-2)' }}>Dashboard</Link> : null}
          </div>

          <div className="brand">Secure Account — Password Reset</div>
        </header>

        {/* TOP NAV: placed above the form box (box = .form-card inside each route) */}
        <div style={{ display:'flex', justifyContent:'center' }}>
          <nav className="top-nav" aria-label="primary navigation">
            {/* Order as requested: Dashboard -> Sign in -> Register -> Login -> Forgot Password */}
            <Link to="/dashboard" className="nav-link">Dashboard</Link>
            <Link to="/login" className="nav-link">Sign in</Link>
            <Link to="/register" className="nav-link">Register</Link>
            <Link to="/login" className="nav-link">Login</Link>
            <Link to="/" className="nav-link">Forgot</Link>
          </nav>
        </div>

        <main className="main-wrap">
          <div className="container">
            <Routes>
              <Route path="/" element={<ForgotPassword />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/reset-password" element={<ResetPassword />} />
              <Route path="/dashboard" element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              } />
            </Routes>
          </div>
        </main>

        <footer>© {new Date().getFullYear()} — Password Reset Demo</footer>
      </div>
    </BrowserRouter>
  );
}
